player.setIfUnset( 'girls_location', 'sw' );
player.setIfUnset( 'banana_location', 'ada' );
player.setIfUnset( 'classroom3_chair_location', 'shelf' );

scene.clearActors();
scene.addActor('Background', 0, 0, 'black-screen');
