scene.addActor('port2', 100, 200, 'invisible');

if( player.get( 'scissors_location' ) === 'desk' ){
  scene.setAnimation('Scissors', 'Scissors', true);
}
if( player.get( 'first_aid_location' ) === 'top' ){
  scene.setAnimation('FirstAid', 'First_Aid', true);
}
if( player.get( 'banana_location' ) === 'sw' ){
  scene.setAnimation('Banana', 'Banana', true);
}
if( player.get( 'girls_location' ) === 'sw' ){
  scene.setAnimation('Girls_act', 'Girls_actor_talking', true);
}
if( player.get( 'classroom3_chair_location' ) === 'shelf' ){
  scene.setActor( 'Chair', 60, 205 );
}
if( player.get( 'book_location' ) !== 'shelf' ){
  scene.setAnimation( 'ShelfBooks', 'Book2', true );
  scene.setActor( 'ShelfBooks', 30, 79 );
} else {
  scene.setAnimation( 'ShelfBooks', 'Classroom3_shelf_books', true );
}
scene.setActorBase('Adalais_act', 98, 192, 'Ada_actor_idle1' );
scene.restoreActorBase( 'Adalais_act' );
scene.setAnimation('Chair','Chair2',true);
scene.setAnimation('Background', 'Classroom3_bg', true);
scene.setAnimation('Foreground', 'Classroom3_fg', true);

#scene.startConversation( 'BlackBar', 'black-bar', [ { name: 'port1', anim: 'Ada_normal' } ] );
#scene.setActor( 'Adalais_act', 126, 71 );
#scene.setAnimation( 'Adalais_act', 'Ada_actor_classroom1_left' );
#scene.setConv( 'port1' );
Adalais: These cubbyhole shelves are here for students so that we can store our backpacks during class.
#scene.setConv( 'port1', 'Ada_smug' );
Adalais: Nobody uses them though, since it is more convenient to have your backpack by your desk.
#scene.setConv( 'port1', 'Ada_normal' );
Adalais: So right now the only thing inside is the school-issued First Aid kit.
#scene.setConv( 'port1', 'Ada_normal' );
Adalais: Should I get it out?

#scene.startConversation( 'BlackBar', 'black-bar', [ { name: 'port1', anim: 'Ada_normal' } ] );
#scene.setActor( 'Adalais_act', 310, 116 );
#//scene.setAnimation( 'Adalais_act', 'Ada_actor_classroom1_right' );
#scene.setConv( 'port1' );
Adalais: It looks like somebody left some scissors on this desk.
#scene.setConv( 'port1', 'Ada_thoughtful' );
Adalais: Perhaps I will take them, just in case I need them.
#scene.setConv( 'port1', 'invisible' );
#scene.setAnimation( 'Scissors', 'invisible' );
...
#scene.setConv( 'port1', 'Ada_normal' );
Adalais: Okay, I have scissors now.
#scene.endConversation();
#scene.restoreActorBase( 'Adalais_act' );

#scene.setConv( 'port1', 'Ada_normal' );
Adalais: Okay...
#scene.setConv( 'port1', 'invisible' );
#scene.setAnimation( 'FirstAid', 'First_Aid' );
...
#scene.setConv( 'port1', 'Ada_normal' );
Adalais: I got the first aid kit out.  Now what?
#scene.endConversation();
#scene.restoreActorBase( 'Adalais_act' );

#scene.setConv( 'port1', 'Ada_normal' );
Adalais: I will leave it where it is for now.
#scene.endConversation();
#scene.restoreActorBase( 'Adalais_act' );

#scene.startConversation( 'BlackBar', 'black-bar', [ { name: 'port1', anim: 'Ada_normal' } ] );
#scene.setActor( 'Adalais_act', 51, 175 );
#scene.setAnimation( 'Adalais_act', 'Ada_actor_classroom1_leftup' );
#scene.setConv( 'port1' );
Adalais: I wish I was tall enough to see what is on that shelf.
#scene.endConversation();
#scene.restoreActorBase( 'Adalais_act' );

#scene.startConversation( 'BlackBar', 'black-bar', [ { name: 'port1', anim: 'Ada_normal' } ] );
#scene.setActor( 'Adalais_act', 126, 71 );
#scene.setAnimation( 'Adalais_act', 'Ada_actor_classroom1_left' );
#scene.setConv( 'port1', 'Ada_normal' );
Adalais: This is the school-issued first aid kit.
#scene.setConv( 'port1', 'Ada_normal' );
Adalais: Inside is only one bandage, a little antiseptic, and a hilariously large amount of cotton balls.
#scene.setConv( 'port1', 'Ada_thoughtful' );
Adalais: It looks like they just stuffed the kit with them to make it look like it was full.
#scene.setConv( 'port1', 'Ada_angry' );
Adalais: Such incompetence...
#scene.endConversation();
#scene.restoreActorBase( 'Adalais_act' );

#scene.startConversation( 'BlackBar', 'black-bar', [ { name: 'port1', anim: 'Ada_normal' } ] );
#scene.setConv( 'port1' );
Adalais: The only people over here are Eva and Mildrid...
#scene.setConv( 'port1', 'Ada_worried' );
Adalais: Ummm... Am I sure I want to go talk to them?  This seems like not a great idea.  They dislike me almost as much as I dislike them.
#scene.setConv( 'port1', 'Ada_annoyed' );
Adalais: Fine... let us just get this over with.
#scene.setActor( 'Adalais_act', 120, 140 );
#scene.setAnimation( 'Adalais_act', 'Ada_actor_classroom1_up' );
#scene.endConversation();
#scene.startConversation( 'BlackBar', 'black-bar', [ { name: 'port1', anim: 'Ada_normal' }, { name: 'port2', anim: 'Girl1_normal' } ] );
#scene.setConv( 'port2' );
Eva: "And that's when I just told her to get lost.  She's not even worth my time you know..."
#scene.setConv( 'port1', 'Ada_talking' );
Adalais: "Excuse me... can I talk to you girls for a second."
#scene.setConv( 'port2' );
Mildrid: "Well well well, look who it is." 
#scene.setConv( 'port2' );
Eva: "What do you want Ada?  Come to bore us with your knowledge?"
#scene.setConv( 'port2' );
Mildrid: "Haha yeah.  I bet she already knew all about calculus before this class period."
#scene.setConv( 'port2' );
Eva: "What a complete dork."
#scene.setConv( 'port2' );
Mildrid: "Just get lost Ada.  You're way too much of a dork for us."
#scene.setConv( 'port1', 'Ada_annoyed' );
Adalais: "Fine."
#scene.restoreActorBase( 'Adalais_act' );
#scene.setConv( 'port1', 'Ada_angry' );
#scene.setAnimation( 'port2', 'invisible' );
Adalais: And people tell me I have a social problem...  I would say 'I told you so', but I think you just wanted to see me suffer.
#scene.restoreActorBase( 'Adalais_act' );
#scene.endConversation();

#scene.startConversation( 'BlackBar', 'black-bar', [ { name: 'port1', anim: 'Ada_normal' } ] );
Adalais: Nobody is over on this side of the room right now.
#scene.endConversation();

#scene.startConversation( 'BlackBar', 'black-bar', [ { name: 'port1', anim: 'Ada_normal' } ] );
#scene.setActor( 'Adalais_act', 296, 116 );
#scene.setAnimation( 'Adalais_act', 'Ada_actor_classroom1_left' );
Adalais: That rancid banana is still over here.  Should I pick it up again?

#scene.setConv( 'Adalais_act', 'Ada_struggle' );
Adalais: Okay, I have the rancid banana again.
#scene.endConversation();
#scene.restoreActorBase( 'Adalais_act' );

#scene.setConv( 'Adalais_act', 'Ada_normal' );
Adalais: I think I should stay away from that for now.
#scene.endConversation();
#scene.restoreActorBase( 'Adalais_act' );

#scene.startConversation( 'BlackBar', 'black-bar', [ { name: 'port1', anim: 'invisible' }, { name: 'port2', anim: 'invisible' }  ] );
#scene.setConv( 'port2', 'Ada_normal' );
Adalais: I would go look at the First Aid kit, except Eva and Mildrid are having a conversation over there.
#scene.setConv( 'port2', 'Ada_annoyed' );
Adalais: I do not like them very much, and I doubt that they would move even if I swallowed my pride and asked them nicely.
#scene.endConversation();

#scene.startConversation( 'BlackBar', 'black-bar', [ { name: 'port1', anim: 'invisible' }, { name: 'port2', anim: 'invisible' }  ] );
#scene.setConv( 'port2', 'Ada_normal' );
Adalais: I would go look at the square shelving, except Eva and Mildrid are having a conversation over there.
#scene.setConv( 'port2', 'Ada_annoyed' );
Adalais: I do not like them very much, and I doubt that they would move at my behest, even if I asked *nicely*.
#scene.endConversation();

#scene.startConversation( 'BlackBar', 'black-bar', [ { name: 'port1', anim: 'Ada_normal' } ] );
#scene.setConv( 'port1', 'Ada_annoyed' );
Adalais: I already moved the chair to the shelf.
#scene.endConversation();
#scene.restoreActorBase( 'Adalais_act' );

#scene.startConversation( 'BlackBar', 'black-bar', [ { name: 'port1', anim: 'Ada_normal' } ] );
#scene.setConv( 'port1', 'Ada_normal' );
Adalais: Should I move the chair to the shelf?

#scene.setConv( 'port1', 'invisible' );
#scene.setAnimation( 'Chair', 'invisible' );
#scene.setAnimation( 'Adalais_act', 'Ada_moving_chair2' );
#scene.setActor( 'Adalais_act', 25, 129 );
...
#scene.setConv( 'port1', 'Ada_normal' );
#scene.setActor('Chair', 60, 205);
Adalais: Okay, I moved the chair.
#scene.setAnimation( 'Chair', 'Chair2' );
#scene.endConversation();
#scene.restoreActorBase( 'Adalais_act' );

#scene.setConv( 'Ada_normal' );
Adalais: I do not need to move that chair yet.
#scene.endConversation();
#scene.restoreActorBase( 'Adalais_act' );

#scene.startConversation( 'BlackBar', 'black-bar', [ { name: 'port1', anim: 'Ada_normal' } ] );
#scene.setActor( 'Adalais_act', 51, 175 );
#scene.setAnimation( 'Adalais_act', 'Ada_actor_classroom1_leftup' );
#scene.setConv( 'port1', 'invisible' );
#scene.setActor( 'Adalais_act', 47, 145 );
Adalais: *hup*
#scene.setConv( 'port1', 'Ada_angry' );
Adalais: Even standing on this chair, I cannot quite reach the shelf.
#scene.setConv( 'port1', 'Ada_normal' );
Adalais: But there is a dangling piece of paper I can pull.
#scene.setAnimation( 'ShelfBooks', 'invisible' );
#scene.setAnimation( 'Adalais_act', 'Ada_actor_pull_book' );
#scene.setActor( 'Adalais_act', -2, 19 )
#scene.setConv( 'port1', 'invisible' );
...
#scene.setConv( 'port1', 'Ada_worried' );
Adalais: Oh dear...  That large book fell down.
#scene.setConv( 'port1', 'Ada_normal2' );
Adalais: I suppose I should pick the book up off the ground, since I was the one who knocked it over.
#scene.setAnimation( 'ShelfBooks', 'Book2' );
#scene.setActor( 'ShelfBooks', 30, 79 );
#scene.setAnimation( 'Adalais_act', 'Ada_actor_classroom1_leftup' );
